// Employee profile/details component
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const EmployeeDetails = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`/api/employees/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployee(response.data.data || response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployee();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!employee) return <p>Employee not found</p>;

  return (
    <div className="employee-details">
      <h1>{employee.firstName} {employee.lastName}</h1>
      <div className="details-container">
        <p><strong>Email:</strong> {employee.email}</p>
        <p><strong>Phone:</strong> {employee.phone}</p>
        <p><strong>Department:</strong> {employee.department}</p>
        <p><strong>Designation:</strong> {employee.designation}</p>
        <p><strong>Joining Date:</strong> {new Date(employee.joiningDate).toLocaleDateString()}</p>
        <p><strong>Salary:</strong> ${employee.salary}</p>
        <p><strong>Address:</strong> {employee.address}, {employee.city}, {employee.state}, {employee.country} {employee.zipCode}</p>
        <p><strong>Emergency Contact:</strong> {employee.emergencyContact}</p>
        <p><strong>Status:</strong> {employee.status}</p>
      </div>
    </div>
  );
};

export default EmployeeDetails;
